import React, {useEffect, useState }from 'react';
import { Input, message, Table, Form, Button } from 'antd';
import useForm from '../../Hook/useForm.js'
function Home() {
  const [form,formedit] = useForm();
  const [data, setData] = useState([]);
  const [valido, setValido] = useState();
  const columns = [
    {
      title: "Numero de Subred",
      dataIndex: "subred",
      key: 'subred',
    },
    {
      title: "Direccion de Subred",
      dataIndex: "direccion",
      key: 'direccion',
    },
    {
      title: "Primer dir IP Host",
      dataIndex: "pip",
      key: 'pip',
    },
    {
      title: "Ultima dir IP Host",
      dataIndex: "uip",
      key: 'uip',
    },
    {
      title: "Dir Brodcast",
      dataIndex: "brodcast",
      key: 'brodcast',
    },
    {
      title: "Mascara",
      dataIndex: "mascara",
      key: 'mascara',
    }

  ]


  const pushear = () =>{
    if(form.direccionIP && form.numSub){
      document.title= form.direccionIP;
      setData([{
        key:1,
        subred: "ds",
        direccion:"ad",
        pip:"da",
        uip:"das",
        brodcast:"da",
        mascara:"asd"
      }]);
      const direccion = form.direccionIP.split(".");
      if(direccion.length !== 4) setValido("Direccion no valida ingresa otra con el formato punteado");
    }else setValido("Porfavor ingresa todos los campos  ")
  }

  return (
    <div>
      <h1>Bienvenido a calcular las direcciones ip</h1>
      <h2> Cual direccion quieres usar? </h2>
      <Form layout = 'horizontal'>
        <Form.Item label="Direccion IP" name = "direccionIP">
          <Input placeholder = "Ingresa tu direccion IP" name="direccionIP"
                  onChange={e => formedit(e)}/>
        </Form.Item>
        <Form.Item label="Numbero de Subredes" name = "numSub">
          <Input placeholder = "Cuantas subredes deseas" name="numSub" type="number"
                  onChange = {e => formedit(e)}/>
        </Form.Item>
        <Form.Item>
          <Button type="primary" onClick={pushear} >Submit</Button>
        </Form.Item>
      </Form>
      <h1>{valido}</h1>
      <Table columns={columns} dataSource={data} />
    </div>
  );
}

export default Home;
