import React from 'react';
import { Input, message, Table } from 'antd';
import useForm from '../../Hook/useForm.js'
function Home() {
  const [form,formedit] = useForm();
  form.direccion = 0;
  const columns = [
    {
      title: "Numero de Subred",
      dataIndex: "subred",
      key: 'subred',
    },
    {
      title: "Direccion de Subred",
      dataIndex: "direccion",
      key: 'direccion',
    },
    {
      title: "Primer dir IP Host",
      dataIndex: "pip",
      key: 'pip',
    },
    {
      title: "Ultima dir IP Host",
      dataIndex: "uip",
      key: 'uip',
    },
    {
      title: "Dir Brodcast",
      dataIndex: "brodcast",
      key: 'brodcast',
    },
    {
      title: "Mascara",
      dataIndex: "mascara",
      key: 'mascara',
    }

  ]
  const data = [
  {
    key: '1',
    subred: 'John Brown',
    direccion: 'as',
    pip: 'New York No. 1 Lake Park',
    uip: "da",
    brodcast: "af",
    mascara: "ad"
  },
];
  const calcular = (data) => {
    let tabla;
    if(data.length > 0){
      tabla = <Table columns={columns} dataSource={data} />
    }
    else tabla = <h1> Ingresa una direccion valida con el formato punteado </h1>
    return tabla
  }
  const tabla = calcular(data);
  return (
    <div>
      <h1>Bienvenido a calcular las direcciones ip</h1>
      <h2> Cual direccion quieres usar? </h2>
      <Input onChange = {console.log("a")}
             enterbutton ="Search"/>
      {tabla}
    </div>
  );
}

export default Home;
