import React, {useEffect, useState }from 'react';
import { Input, message, Table, Form, Button, InputNumber } from 'antd';
import useForm from '../../Hook/useForm.js'
function Home() {
  const [form,formedit] = useForm();
  const [data, setData] = useState([]);
  const [valido, setValido] = useState();
  const columns = [
    {
      title: "Numero de Subred",
      dataIndex: "subred",
      key: 'subred',
    },
    {
      title: "Direccion de Subred",
      dataIndex: "direccion",
      key: 'direccion',
    },
    {
      title: "Primer dir IP Host",
      dataIndex: "pip",
      key: 'pip',
    },
    {
      title: "Ultima dir IP Host",
      dataIndex: "uip",
      key: 'uip',
    },
    {
      title: "Dir Brodcast",
      dataIndex: "brodcast",
      key: 'brodcast',
    },
    {
      title: "Mascara",
      dataIndex: "mascara",
      key: 'mascara',
    }

  ]


  const pushear = () =>{
    if(form.direccionIP && form.numSub){
      const direccion = form.direccionIP.split(".");
      const numSub = parseInt(form.numSub,10);
      let isnum = /^\d+$/.test(numSub);
      if(!isnum) setValido("Ingresa un numero de subredes valido");
      if(direccion.length !== 4) setValido("Direccion no valida ingresa otra con el formato punteado");
      else{
        if(checarDireccion(direccion)){
          calcularClase(parseInt(direccion[0],10),numSub);
        }
      }

    }else setValido("Porfavor ingresa todos los campos  ")
  }

  const checarDireccion = (direccion) =>{
    for(let i =0 ; i<direccion.length; i++){
      const valid = /^\d+$/.test(direccion[i]);
      if(!valid || direccion[i].length !== 4 ){
        setValido("Direccion no valida ingresa otra con el formato punteado puto");
        return false;
      }
    }
    return true;
  }

  const calcularClase = (primerO, numSub) =>{
    const bits = calcularBitsSub(numSub);
    console.log(primerO);
    if(primerO<=127) setValido("Red Clase A con " + bits + " para subnetting");
    else if(primerO > 127 && primerO <= 191) setValido("Red Clase B con " + bits + " para subnetting");
    else if(primerO > 191 && primerO <= 223) setValido("Red Clase C con " + bits + " para subnetting");
    else if(primerO > 223 && primerO <= 239) setValido("Red Clase D con " + bits + " para subnetting");
    else if(primerO > 240 && primerO <= 255) setValido("Red Clase E con " + bits + " para subnetting");
    else setValido("Red no valida ingresa otra");
  }

  const calcularBitsSub = (numSub) =>{
    let numero = 1;
    while(2**numero < numSub) numero++;
    return numero;
  }

  return (
    <div>
      <h1>Bienvenido a calcular las direcciones ip</h1>
      <h2> Cual direccion quieres usar? </h2>
      <Form layout = 'horizontal'>
        <Form.Item label="Direccion IP" name = "direccionIP">
          <Input placeholder = "Ingresa tu direccion IP" name="direccionIP"
                  onChange={e => formedit(e)}/>
        </Form.Item>
        <Form.Item label="Numbero de Subredes" name = "numSub">
          <Input placeholder = "Cuantas subredes deseas" name="numSub" type="number"
                  onChange = {e => formedit(e)}/>
        </Form.Item>
        <Form.Item>
          <Button type="primary" onClick={pushear} >Submit</Button>
        </Form.Item>
      </Form>
      <h1>{valido}</h1>
      <Table columns={columns} dataSource={data} />
    </div>
  );
}

export default Home;
