import React from 'react';
import { Input, message, Table, Form, Button } from 'antd';
import useForm from '../../Hook/useForm.js'
function Home() {
  const [form,formedit] = useForm();
  form.direccionIP = 0;
  form.numSub = 0;
  const columns = [
    {
      title: "Numero de Subred",
      dataIndex: "subred",
      key: 'subred',
    },
    {
      title: "Direccion de Subred",
      dataIndex: "direccion",
      key: 'direccion',
    },
    {
      title: "Primer dir IP Host",
      dataIndex: "pip",
      key: 'pip',
    },
    {
      title: "Ultima dir IP Host",
      dataIndex: "uip",
      key: 'uip',
    },
    {
      title: "Dir Brodcast",
      dataIndex: "brodcast",
      key: 'brodcast',
    },
    {
      title: "Mascara",
      dataIndex: "mascara",
      key: 'mascara',
    }

  ]
  const data = [];
  const calcular = (data) => {
    let tabla;
    if(data.length != 0){
      tabla = <Table columns={columns} dataSource={data} />
    }
    else tabla = <h1> Ingresa una direccion valida con el formato punteado </h1>
    return tabla
  }
  let tabla = calcular(data);

  const pushear = () =>{
    data.push({
      subred: "ds",
      direccion:"ad",
      pip:"da",
      uip:"das",
      brodcast:"da",
      mascara:"asd"
    });
    tabla = calcular(data);
    console.log(form)
    console.log(formedit)
  }

  return (
    <div>
      <h1>Bienvenido a calcular las direcciones ip</h1>
      <h2> Cual direccion quieres usar? </h2>
      <Form layout = 'horizontal'>
        <Form.Item label="Direccion IP" name = "direccionIP">
          <Input placeholder = "Ingresa tu direccion IP"
                  onChange={e => formedit(e)}/>
        </Form.Item>
        <Form.Item label="Numbero de Subredes" name = "numSub">
          <Input placeholder = "Cuantas subredes deseas" name="numSub"
                  onChange = {e => formedit(e)}/>
        </Form.Item>
        <Form.Item>
          <Button type="primary" onClick={pushear} >Submit</Button>
        </Form.Item>
      </Form>
      {tabla}
    </div>
  );
}

export default Home;
